import { useState, useEffect } from "react";
import axios from "axios";
import { CiCirclePlus } from "react-icons/ci";
import { IconContext } from "react-icons";
import "./index.css";
import LoadBoards from "./components/BoardTasks";
import PomodoroTimer from "./components/PomodoroTimer";
import RoomManager from "./components/RoomManager";
import JoinRoom from "./components/JoinRoom";

function App() {
  const [isAdding, setIsAdding] = useState(false);
  const [roomID, setRoomID] = useState(null);
  const [userID, setUserID] = useState(3);

  useEffect(() => {
    // Check the URL for a room ID
    const urlRoomID = new URL(window.location.href).pathname.split("/").pop();
    if (urlRoomID !== "App" && urlRoomID !== "") {
      setRoomID(urlRoomID);
    }
  }, []);

  const addBoard = async (isShared) => {
    try {
      await axios.post(
        "http://localhost:5000/add_board",
        {
          board_name: "New Board",
          user_id: userID,
          is_shared: isShared,
          room_id: roomID,
        },
        {
          headers: { "Content-Type": "application/json" },
        }
      );

      setIsAdding(true);
    } catch (error) {
      console.error("Error adding board:", error);
    }
  };

  const addNewBoard = async (isShared) => {
    await addBoard(isShared);
  };

  return (
    <main>
      <RoomManager roomID={roomID} setRoomID={setRoomID}/>
      <br></br>
      {roomID ? (
        <div className="flex columns-2 gap-5 mx-4 ">
          <div className="flex gap-10">
            <LoadBoards
              isAdding={isAdding}
              setIsAdding={setIsAdding}
              userID={userID}
              shared={true}
              roomID={roomID}
            />
            <div>
            <LoadBoards
              isAdding={isAdding}
              setIsAdding={setIsAdding}
              userID={userID}
              shared={false}
              roomID={roomID}
            />
            </div>
            
            <button
              className="btn btn-neutral flex"
              onClick={() => addNewBoard(false)}
            >
              <IconContext.Provider value={{ size: "30" }}>
                <CiCirclePlus />
              </IconContext.Provider>
              <p className="text-lg">Add new board</p>
            </button>
            <button
              className="btn btn-neutral flex"
              onClick={() => addNewBoard(true)}
            >
              <IconContext.Provider value={{ size: "30" }}>
                <CiCirclePlus />
              </IconContext.Provider>
              <p className="text-lg">Add new shared board</p>
            </button>
          </div>
        </div>
      ) : (
        <JoinRoom />
      )}
    </main>
  );
}

export default App;
